void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized coordinates
    vec2 uv = fragCoord / iResolution.xy;

    // Create varying waves with different frequencies and amplitudes
    float time = iTime;

    float freq1 = 1.0;
    float amp1 = 0.1;
    float wave1 = sin(uv.x * freq1 + time * 0.2) * amp1;

    float freq2 = 2.0;
    float amp2 = 0.15;
    float wave2 = sin(uv.x * freq2 + time * 0.1) * amp2;

    float freq3 = 3.0;
    float amp3 = 0.2;
    float wave3 = sin(uv.x * freq3 + time * 0.05) * amp3;

    float animation = (wave1 + wave2 + wave3) * 0.5 + 0.5;

    // Varying shades of blue based on the y-coordinate
    vec3 lakeColor = vec3(0.0, 0.4, 0.7); // Dark blue color
    vec3 gradient = vec3(0.0, 0.1, 0.7); // Adjust gradient color
    float yGradient = mix(0.0, 1.0, uv.y); // Linear gradient based on y-coordinate
    vec3 baseColor = mix(lakeColor, lakeColor + gradient, yGradient);

    // Add variety to blue color by changing hue, saturation, and lightness
    float hue = 0.6;      // Adjust hue value
    float saturation = 0.8; // Adjust saturation value
    float lightness = 0.5;  // Adjust lightness value

    // Adjust RGB directly for color variety
    vec3 finalColor = mix(baseColor, baseColor * vec3(1.0 + hue, saturation, lightness), 0.5);

    // Combine with animated water effect
    finalColor *= animation;

    // Add simple circular sand island
    vec2 islandCenter = vec2(0.5, 0.5); // Center of the island
    float islandRadius = 0.1;            // Radius of the island

    // Calculate distance to the island center
    float distanceToIsland = distance(uv, islandCenter);

    // Create a circular mask for the island
    float islandMask = smoothstep(islandRadius, islandRadius + 0.02, islandRadius - distanceToIsland);

    // Sand color for the island
    vec3 sandColor = vec3(0.9, 0.8, 0.6); // Adjust sand color

    // Mix sand color with the existing color based on the island mask
    finalColor += (sandColor - finalColor) * islandMask;

    // Output final color
    fragColor = vec4(finalColor, 1.0);
}
