// from https://www.shadertoy.com/view/XdfGDH
float normpdf(in float x, in float sigma)
{
	return 0.39894*exp(-0.5*x*x/(sigma*sigma))/sigma;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // from https://www.shadertoy.com/view/XdfGDH
    const int mSize = 13;
    const int kSize = (mSize-1)/2;
    float kernel[mSize];
    vec3 final_color = vec3(0.0);

    //create the 1-D kernel
    float sigma = 20.0;
    float Z = 0.0;
    for (int j = 0; j <= kSize; ++j) {
        kernel[kSize+j] = kernel[kSize-j] = normpdf(float(j), sigma);
    }

    //get the normalization factor (as the gaussian has been clamped)
    for (int j = 0; j < mSize; ++j) {
        Z += kernel[j];
    }

    //read out the texels
    for (int i=-kSize; i <= kSize; ++i) {
        for (int j=-kSize; j <= kSize; ++j) {
            final_color += kernel[kSize+j]*kernel[kSize+i]*texture(iChannel0, (fragCoord.xy+vec2(float(i),float(j))) / iResolution.xy).rgb;
        }
    }

    vec2 uv = fragCoord/iResolution.xy;
    vec4 unblurredColor = texture(iChannel0, uv);

    fragColor = vec4(mix(unblurredColor.rgb, final_color/(Z*Z), unblurredColor.a), 1.0);

    float vignette = smoothstep(1.75, 0.0, length((2.0 * uv) - vec2(1.0)));
    fragColor = mix(vec4(0.0, 0.0, 0.1, 1.0), fragColor, vignette);
}

const int RAY_STEPS = 256;
const float HALF_PI = 3.14159 * 0.5;
const float DEG_TO_RAD = 3.14159 / 180.0;
const float FOCAL_LENGTH = 16.0;
const float FOCAL_RANGE = 16.0;

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

vec3 rotateX(vec3 p, float a) {
    return vec3(p.x, cos(a) * p.y - sin(a) * p.z, sin(p.y) + cos(p.z));
}

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

void raycast(vec2 uv, out vec3 dir, out vec3 eye, out vec3 ref) {
    eye = vec3(0.0, 4, -18.0);
    ref = vec3(0.0, 2.5, 0.0);

    float len = tan(3.14159 * 0.125) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * iResolution.x / iResolution.y;
    vec3 p = ref + uv.x * H + uv.y * V;
    dir = normalize(p - eye);
}

float sphere(vec3 p, float r, vec3 c)
{
    return distance(p, c) - r;
}

float torus(vec3 p, vec2 t)
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q) - t.y;
}

float cone(vec3 p, vec2 c)
{
    // c must be normalized
    float q = length(p.xy);
    return dot(c,vec2(q,p.z));
}

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}

float plane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

#define BACK_WALL plane(pos, vec4(0.0, 0.0, -1.0, 5.0))
#define LEFT_WALL plane(pos, vec4(1.0, 0.0, 0.0, 5.0))
#define RIGHT_WALL plane(pos, vec4(-1.0, 0.0, 0.0, 5.0))
#define CEILING plane(pos, vec4(0.0, -1.0, 0.0, 7.5))
#define FLOOR plane(pos, vec4(0.0, 1.0, 0.0, 2.5))
#define TALL_CUBE box(rotateY(pos + vec3(2.0, 0.0, -3.0), 27.5 * DEG_TO_RAD), 0.5 * vec3(3.0, 6.0, 3.0))
#define CUBE box(rotateY(pos + vec3(-2.0, 1.0, -0.75), -17.5 * DEG_TO_RAD), 0.5 * vec3(3.0, 3.0, 3.0))

#define BACK_WALL_NUM 0
#define LEFT_WALL_NUM 1
#define RIGHT_WALL_NUM 2
#define CEILING_NUM 3
#define FLOOR_NUM 4
#define TALL_CUBE_NUM 5
#define CUBE_NUM 6

float sceneMap3D(vec3 pos, vec3 lightPos)
{
    float t = BACK_WALL;
    t = min(t, LEFT_WALL);
    t = min(t, RIGHT_WALL);
    t = min(t, CEILING);
    t = min(t, FLOOR);
    t = min(t, TALL_CUBE);
    t = min(t, CUBE);
    return t;
}

void sceneMap3D(vec3 pos, out float t, out int obj, vec3 lightPos)
{
    t = BACK_WALL;
    float t2;
    obj = BACK_WALL_NUM;
    if((t2 = LEFT_WALL) < t) {
        t = t2;
        obj = LEFT_WALL_NUM;
    }
    if((t2 = RIGHT_WALL) < t) {
        t = t2;
        obj = RIGHT_WALL_NUM;
    }
    if((t2 = CEILING) < t) {
        t = t2;
        obj = CEILING_NUM;
    }
    if((t2 = FLOOR) < t) {
        t = t2;
        obj = FLOOR_NUM;
    }
    if((t2 = TALL_CUBE) < t) {
        t = t2;
        obj = TALL_CUBE_NUM;
    }
    if((t2 = CUBE) < t) {
        t = t2;
        obj = CUBE_NUM;
    }
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
    	float m;
        sceneMap3D(pos, m, hitObj, lightPos);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

vec3 computeNormal(vec3 pos, vec3 lightPos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                            sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                            sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 light, vec3 view) {
    float lambert = max(0.0, dot(n, light)) + 0.2;

    switch(hitObj) {
        case BACK_WALL_NUM:
        case CEILING_NUM:
        case FLOOR_NUM:
        case TALL_CUBE_NUM:
        return vec3(0.85, 0.81, 0.78) * lambert; 
        break;
        case CUBE_NUM:
        return vec3(0.85, 0.81, 0.78) * lambert; 
        break;
        case LEFT_WALL_NUM:
        return vec3(0.63, 0.065, 0.05) * lambert;
        break;
        case RIGHT_WALL_NUM:
        return vec3(0.14, 0.45, 0.091) * lambert;
        break;
        case -1:
        // Background
        return vec3(0.0);
        break;
    }
    return vec3(1.0);
}

Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));

    return Intersection(t, surfaceColor, isect, hitObj);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 lightPos = vec3(0.0, 7.45, 0.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // [-1, 1]
    vec2 uv2 = 2.0 * uv - vec2(1.0);

    Intersection aaIsects[4];
    vec3 dir, eye, ref;
    int idx = 0;
    for(float i = 0.0; i < 1.0; i += 0.5) {
        for(float j = 0.0; j < 1.0; j += 0.5) {
            raycast(uv2 + vec2(i, j) / iResolution.xy, dir, eye, ref);
            aaIsects[idx++] = sdf3D(dir, eye, lightPos);
        }
    }

    // animate focal plane
    float len = FOCAL_LENGTH - 8.0 * cos(iTime);

    // calculate distance from eye to intersection
    float dist = abs(dot(normalize(ref - eye), (aaIsects[0].p - eye)));
    float dofZ = min(1.0, abs(dist - len) / FOCAL_RANGE);

    vec3 avgColor = vec3(0.0);
    for(int i = 0; i < 4; ++i) {
        avgColor += aaIsects[i].color;
    }
    avgColor *= 0.25;

    fragColor = vec4(avgColor * step(0.0, aaIsects[0].t), dofZ);
}
