float func(float x){
float r = 0.0; 
r+=abs(-0.3+sin(iTime+x))*2.0;
r+=abs(-0.3+sin(-iTime+x))*2.0;
r*=r;
r/=6.0;
return r;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 R = iResolution.xy;
    vec2 uv = (fragCoord-.5*R.xy)/R.y;
    uv.y=-uv.y;
    float y0 = uv.y;
    float y1 = uv.y;
    vec3 col = vec3(0.0);
    for(int i = 0; i<17; i++){
        float fi = float(i);
        y1=uv.y+1.5-0.1*fi;

        y1+=0.1*-(0.5+(sin(uv.x+t)*sin(uv.x+t)))*func(t+10.0*uv.x+fi*2.0);

        y0 = y0*step(step(0.4,y1),0.01)+y1*step(0.4,y1);
    }
    col.g=(1.0-fract(y0*12.0*(0.1+pow(sin(0.3*t+1.0),2.0))));
    col.r=(1.0-fract(y0*6.0*(0.1+pow(sin(0.6*t+2.0),2.0))));
    col.b=(1.0-fract(y0* 3.0*(0.1+pow(sin(0.9*t+3.1),2.0))))*(sin(t)*sin(t));
    //col+=1.0-step(0.41,y0);
    fragColor = vec4(col,1.0);
}
