float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

const float pi = 3.14159;
const float pi2 = 1. * pi / 48.;
const float pi3 = 2. * pi / 48.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

float t = 0.5  * iTime + 0.1 * cos(iTime) + 1000.;
vec2 dir = cos(0.01 * uv.y * t) * sin( 0.01 * uv.x * t) * (uv.xy - 0.5);
uv = 2. * uv - 1.;
float n = 4.;

float d =cos(t) * length(dir);
dir *=  (3. + pow(cos(t),5.)) * (1. - d);
float theta = atan(dir.y,dir.x) + pi;
float r = cos(0.1 * t) * 2. * pi / n;
float k = cos(floor(.5+theta /r)*r-theta)*length(dir);


float p = (1. + 0.06 * cos(t) * random(uv))  *cos((4. *theta + t));
float p2 = (1. + 0.06 * cos(t + pi2) * random(uv)) * cos((4. *theta + t + pi2));
float p3 = (1. + 0.06 * cos(t + pi3) * random(uv)) * cos((4. *theta + t + pi3));
float v = sqrt(p2 * p2  + p * p + p3 * p3);
float val = sqrt( p + v);
float val2 = sqrt(p2 + v);
float val3 = sqrt(p3 + v);


    // Time varying pixel color
    vec3 col = vec3(smoothstep( k ,smoothstep(k,0.3,(1. + 0.5 * cos(t)) * val),0.5*val),
    smoothstep( k ,smoothstep(k,0.3,(1. + 0.5 * cos(t)) * val2),0.5*val2),
    smoothstep( k ,smoothstep(k,0.3,(1. + 0.5 * cos(t)) * val3),0.5*val3)   
    );

 vec3 col2 = vec3(smoothstep( k ,smoothstep(k,0.5 + 0.5 * cos(0.1 *t),(1. + 0.5 * cos(t)) * p),0.5*p),
    smoothstep( k ,smoothstep(k,0.5 + 0.5 * cos(0.1 * t),(1. + 0.5 * cos(t + pi2)) * p2),0.5* p2),
    smoothstep( k ,smoothstep(k,0.5 + 0.5 * cos(0.1 * t),(1. + 0.5 * cos(t + pi3)) * p3),0.5*p3)   
    );

col = mix(col2,col,0.5 + 0.5 * cos(0.5 * t));

col *= (1. - d);

    // Output to screen



    fragColor = vec4(col,1.0);
}
