float plot(vec2 st, float offset) {    
    return smoothstep(0.0662, 0.017, abs(abs(sin(iTime+offset)) - st.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = fragCoord.xy/iResolution.xy;    

    float pct = plot(st, 0.0);        
    vec3 color = pct*vec3(0.0,1.0,0.0);
    float pct2 = plot(st, 0.1);
    color = color + pct2*vec3(1.0,0.0,0.0);
    float pct3 = plot(st, 0.2);
    color = color + pct3*vec3(0.0,0.0,1.0);

	fragColor = vec4(color,1.0);
}
