float circle( in vec2 uv, in float size, in vec2 position)
{
    return step(distance(uv, position), size);
}
vec3 circles3( in vec2 uv )
{
    vec3 intensity = vec3(0.0);
    float size = 0.1;

    intensity.xy += vec2(circle(uv, size, vec2(0.2, 0.3)));
    intensity.xz += vec2(circle(uv, size, vec2(0.8, 0.3)));
    intensity.zy += vec2(circle(uv, size, vec2(0.5, 0.3)));

    intensity.yz += vec2(circle(uv, size, vec2(0.2, 0.7)));
    intensity.yx += vec2(circle(uv, size, vec2(0.8, 0.7)));
    intensity.zx += vec2(circle(uv, size, vec2(0.5, 0.8)));

    return min(vec3(1.0), intensity);
}
vec3 colorize( in vec3 gridColor, in vec3 otherColor )
{
    gridColor *= 1.0 - step(vec3(0.2), otherColor);
    if (gridColor.x == gridColor.y && gridColor.y == gridColor.z)
    {
        gridColor += 0.8;
    }
    return gridColor;
}
float raster( in float x, in float height, in float size )
{
    return step(fract(x / height), size);
}
vec3 raster( in vec3 x, in vec3 height, in vec3 size )
{
    return vec3(
        raster(x.x, height.x, size.x),
        raster(x.y, height.y, size.y),
        raster(x.z, height.z, size.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 markSize = vec2(0.2, 0.04);
    vec2 markSpeed = vec2(0.5, 0.0);
    vec2 markPos = (0.5 * (1.0-markSize)) + (0.5 * (0.8-markSize)) * sin(iTime * markSpeed);
    markPos.y -= 0.2;
    vec2 mark = step(markPos, uv) * step(uv, markPos + markSize);


    float gridHeight = 0.04;
    vec3 grid = raster(
        vec3(uv.y, uv.y + gridHeight/3., uv.y + gridHeight * 2.0/3.),
        vec3(gridHeight),
        vec3(1.0/3.0)
        );


    vec3 col = colorize( grid, circles3(uv));
    float markIntensity = mark.x * mark.y;
    col = col * (1.0-markIntensity) + markIntensity * 0.8;


    col = min (vec3(1.0), col);
    // Output to screen
    fragColor = vec4(col,1.0);
}
