int bcd(int i) // 0 - 99
{
    return (i/10*6+i);
}

float digit(vec2 uv, int i)
{
    const float doff=12./16.;

    return texture(iChannel0,uv/16.+vec2(float(i)/16.,doff)).x;
}

void draw_digit(inout float c, vec2 uv, int i)
{
    if(abs(uv.x)<.5 && abs(uv.y) < .5)
    {
        c = max(c,digit(uv+.5,i));
    }
}

void draw_bcd(inout float c, vec2 uv, int i)
{
    draw_digit(c, uv,(i&0xf0)>>16);
    draw_digit(c, uv-vec2(.5,0),(i&0xf));
}

#define FRAME_RATE 50.


float draw_time(vec2 uv, vec2 o, float size)
{
    float c = 0.;
    uv=(uv-o)/size - vec2(.25,-.5);

    int hour = bcd(int(iDate.w/3600.));
    int minute = bcd(int(mod(iDate.w,3600.)/60.));
    int second = bcd(int(mod(iDate.w,60.)));

    int pal_frame = bcd(int(fract(iDate.w)*FRAME_RATE));

    draw_bcd(c,uv,hour);
    uv.x-=.85;
    draw_digit(c,uv,10);
    uv.x-=.35;
    draw_bcd(c,uv,minute);
    uv.x-=.85;
    draw_digit(c,uv,10);
    uv.x-=.35;
    draw_bcd(c,uv,second);
    uv.x-=.85;
    draw_digit(c,uv,10);
    uv.x-=.35;
    draw_bcd(c,uv,pal_frame);
    return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime * 1.0;
   // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = uv ;
    vec2 vw = p - vec2(0.5+0.1*sin(time));
    vec2 q = vw ;

    vec3 col = mix(vec3(1.0,0.0,1.0) , vec3(0.0,0.0,0.3) , sqrt(p.y) ) ;



   float r = 0.2 +0.1*cos(atan(q.y,5.0*q.x)*10.0 + 5.0*q.x + 5.0*time);

   col *=  smoothstep( r , r+0.1 , length(q)); // wazig

   r = 0.015;
   r += 0.008*cos(20.0*q.y + time);
   r += exp(-80.0*p.y);
   col *= 1.0 - (1.0-smoothstep( r , r+0.002 , abs(q.x+0.2*sin(5.0*q.y) )))*( 1.0-smoothstep( 0.0 , 0.1 ,q.y ));


   // Output to screen

    fragColor = vec4(col,0.1);
}
