// https://www.shadertoy.com/view/sd2yD1 square minus circle subdiv 3d 2022-1-29 by Jakob Thomsen 
// Combining rodolphito's Square minus Circle https://www.shadertoy.com/view/stlXWH
// with the subdivision from my 3dified binary tree https://www.shadertoy.com/view/7dByz1
// (2d sdf here: https://www.shadertoy.com/view/sdjyD1 Binary Tree SDF)

#define EPSILON 0.001
#define DIST_MAX 500.0

// https://iquilezles.org/articles/distfunctions2d
float halfspace(vec3 p, float d)
{
    return p.z - d;
}

// https://www.shadertoy.com/view/stlXWH Square minus Circle - distance by rodolphito
// The MIT License
// Copyright Â© 2021 Rodol Phito
// Permission is hereby granted, free of charge, to any person obtaining a copy of this software and associated documentation files (the "Software"), to deal in the Software without restriction, including without limitation the rights to use, copy, modify, merge, publish, distribute, sublicense, and/or sell copies of the Software, and to permit persons to whom the Software is furnished to do so, subject to the following conditions: The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software. THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY, FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM, OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS IN THE SOFTWARE.
float square_minus_circle(vec2 p) 
{
    float b = 0.0;
    p = abs(p); p = (p.y>p.x) ? p.yx : p.xy;
    if (p.y<0.0) return length(p-vec2(1.,0.0));
    return max(length(max(p-1.0,0.0)) + min(p.x-1.0,0.0),sqrt(1.0)-length(p));
}

float square_minus_circle_subdiv2d(vec2 p)
{
    float s = exp2(ceil(-log2(p.y))); // scale
    vec2 l = fract(p*s); // local coordinates
    //float d = length(-0.5+l)/s; // circle
    float d = square_minus_circle(l*2.0 - 1.0)/2.0 / s;
    return (p.y < 0.0) ? -p.y : d;
}

float square_minus_circle_subdiv3d(vec3 p)
{
    p.z *= -1.0;
    p.z += 1.0;
    float dp = p.y; // distance to plane
    float df = square_minus_circle_subdiv2d(p.xz); // distance to 2d SDF
    //return sqrt(dp*dp+df*df); // 3dify/sweep/insulate as in https://www.shadertoy.com/view/7dByz1

    // extrusion as discribed in https://iquilezles.org/articles/distfunctions
    float h = 0.25;
    vec2 w = vec2(df, abs(p.y) - h);
    return min(max(w.x,w.y), 0.0) + length(max(w, 0.0));
}

float map(vec3 p)
{
    float d = mix(0.01, 0.01, 0.5 + 0.5 * cos(iTime));
    return
        min
        (
            square_minus_circle_subdiv3d(p) - d,
            halfspace(p, -1.0)
        );
}

// https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal(vec3 p)
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return
        normalize
        (
            k.xyy * map(p + k.xyy*h)
            +
            k.yyx * map(p + k.yyx*h)
            +
            k.yxy * map(p + k.yxy*h)
            +
            k.xxx * map(p + k.xxx*h)
        );
}

float trace(vec3 ro, vec3 rd)
{
    for(float t = 0.0; t < DIST_MAX;)
    {
        float h = map(ro + rd * t);
        if(h < EPSILON)
            return t;
        t += h;
    }
    return DIST_MAX;
}

// https://iquilezles.org/articles/rmshadows
float shadow( in vec3 ro, in vec3 rd, float mint, float maxt )
{
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t);
        if( h<EPSILON )
            return 0.0;
        t += h;
    }
    return 1.0;
}

// https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor );
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

#define pi 3.1415926

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 ndc = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    ndc.x *= iResolution.x / iResolution.y;
    vec2 m = length(iMouse.xy) <= 10. // click in lower left corner...
           ? pi * vec2(2.0 * fract(iTime * 0.01), 2.5 / 4.0) // ...for demo mode
           : pi * vec2(2.0 * iMouse.x / iResolution.x, 0.5 + 0.5 * iMouse.y / iResolution.y); // mouse look

    mat2 R = mat2(cos(m.x), sin(m.x), -sin(m.x), cos(m.x));
    vec3 ro = vec3(0.0, 0.0,-5.0);
    mat2 S = mat2(cos(m.y), sin(m.y), -sin(m.y), cos(m.y));
    ro.yz = S * ro.yz;
    ro.xy = R * ro.xy;

    vec3 rd = normalize(vec3(0.5 * ndc.xy, 1.0)); // NOTE: omitting normalization results in clipped edges artifact
    rd.yz = S * rd.yz;
    rd.xy = R * rd.xy;

    float dist = trace(ro, rd);
    vec3 dst = ro + rd * dist;
    vec3 n = normal(dst);

    vec3 lightdir = normalize(vec3(0.0,-1.0, 1.0));
    vec3 ambient = vec3(0.4);
    float brightness = max(dot(lightdir, n), 0.0);
    brightness *= shadow(ro+rd*dist,lightdir, 0.01, DIST_MAX);
    vec3 color = vec3(1.0);
    color *= (n * 0.5 + 0.5);
    color = (ambient * calcAO(dst, n) + brightness) * color;

    fragColor = (dist < DIST_MAX) ? vec4(color, 1.0) : vec4(0.0);
    fragColor = sqrt(fragColor); // approximate gamma
}
