        float dot_pr(vec2 v1, vec2 v2)
       {
            return v1.x * v2.x + v1.y * v2.y;
       }

    bool isin_segCircle(vec2 p, SegCircle c)
    {
        if (abs(d2((p.x - c.center.x)/c.wh) + d2(p.y - c.center.y) - d2(c.radius)) > c.e)
            return false;

        float a = -PI / 2.0 + float(c.angle) * PI / 2.0;
        vec2 n = vec2(cos(a) * c.radius, sin(a) * c.radius);
        return dot_pr(n, p - c.center) > 0.0;
    }

    bool ison_horline(vec2 p, HorLine l)
    {
        return abs(p.y - l.y) < 0.003 && p.x >= l.from && p.x <= l.to;
    }

    bool ison_vertline(vec2 p, VertLine l)
    {
        return abs(p.x - l.x) < 0.003 * iResolution.y / iResolution.x
            && p.y >= l.from && p.y <= l.to;
    }



    bool close_to_line(vec2 p, SegLine l)
    {
        float xc = (p.x - l.p1.x) / (l.p2.x - l.p1.x);
        float yc = (p.y - l.p1.y) / (l.p2.y - l.p1.y);
         return abs(xc - yc) < 0.07 && p.x >= l.from && p.x <= l.to;
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy; 
    int i;
    vec3 col = vec3(1.0);

     if (mod(uv.x+0.001, 1.0 / 32.0) < 0.002
             ||abs(mod(uv.y, 1.0 / 18.0)) < 0.002) {
       col = vec3(1.0, 1.0, 1.0);
     }

      for (i = 0; i < lines.length(); ++i) {
        if (close_to_line(uv, lines[i]))
          col = vec3(0);
      }  

       for (i = 0; i < sc.length(); ++i) {
         if (isin_segCircle(uv, sc[i]))
           col = vec3(0);
       }

        for (i = 0; i < hlines.length(); ++i) {
          if (ison_horline(uv, hlines[i]))
            col = vec3(0);
        }

         for (i = 0; i < vlines.length(); ++i) {
           if (ison_vertline(uv, vlines[i]))
             col = vec3(0);
         }

          fragColor = vec4(col,1.0);
}
