//please check https://en.wikipedia.org/wiki/Kernel_(image_processing)#Convolution for kernel
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy ;
    //t = vec4(uv, 0, 1.);
    // Output to screen
    fragColor = vec4(texture(iChannel1, uv).rgb, 1.);//vec4(col.r, col.r, col.r, 1.0);
}

mat3 kernel = mat3(
    0.0, -1., 0.0,
    -1., 4.0, -1.,
    0.0, -1., 0.0
);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //fragColor = vec4(1.0,0.0,0.0,1.0);
        // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy*sin(iTime);
    vec2 uv = fragCoord/iResolution.xy ;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 color = vec4(0.0);
    vec2 texel = 1.0 / iResolution.xy;
    for (int y = 0; y < 3; y++)
    for (int x = 0; x < 3; x++)
        color += texture(iChannel0, uv + texel * vec2(x - 1, y - 1)) * kernel[x][y];

    color *= 3.0;


    // Time varying pixel color
    //col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = color;
}
