void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 array[6] = vec3[6](vec3(1.,0.,0.),vec3(1.,0.,1.),vec3(0.,0.,1.),vec3(0.,1.,1.),vec3(0.,1.,0.),vec3(1.,1.,0.));

    int color = int(ceil(uv.y*6.)-1.);

    // Output to screen
    fragColor = vec4(array[color],1.0);
}
