renderpass.code:
float A = .9, t, d=0.;
#define f(x)   ( d+ x + A*cos(x-t) )   // function to inverse
#define df(x)  (    1.- A*sin(x-t) )   // first derivative
#define ddf(x)       ( -A*cos(x-t) )   // second derivative

float invF(float x) {    // --- iterative solving the inverse function
    float y0 = x-d, D;
    for (int i = 0; i < 3; i++ )
#if 0                                  // using order1 Taylor series : slow conv if A > .6 
        y0 += ( x-f(y0) ) / df(y0);
#else                                  // using order2 Taylor series : fast convergence
        D  = df(y0)*df(y0) -2.*ddf(y0)*(f(y0)-x),
        y0 += ( -df(y0) + sqrt(D) ) / ddf(y0);
#endif
    return y0;
}

#define S(v)  smoothstep( 1.5*min(fwidth(v),.1), 0., v )
#define Sa(v) smoothstep( 1.5*fwidth(v), 0., abs(v) )

void mainImage( out vec4 O, vec2 u ) // ----------------------------------
{
    vec2 R = iResolution.xy,
         U = 10.*( 2.*u - R ) / R.y, U0=U;
    O-=O;
    O.r = Sa( U.y - invF(U.x) );         // red: plot the inverse function

    t = iTime;
    U.x = invF(U.x);
    O.g = Sa( U.y - A*sin(U.x-t) );      // green: plot trochoid

    if (U0.x<0.) O.b = S( length( fract(U)-.5) -.1 ); // blue: displace points.x as f()
    else {                                    // right: without dot distortion
        float v = df(floor(U.x));
        U.x = U0.x - f(floor(U.x)),
        O.b = S( length( vec2(U.x-.5*v, fract(U.y)-.5) ) -.1 );
        O.g += .5*O.b;
    }
}
