void mainImage( out vec4 fragColor, in vec2 fragCoord )

{

    vec4 initialcolor = vec4(0.6,0.6,0.6,1.0);
    vec4 replacecolor = vec4(1.0,0.0,0.302,1.0);
    vec4 bgcolor = vec4(0.0,0.0,0.0,0.0);


    float tolerance = 4.0; //this will probably need to be changed when porting to notitg!!!!
    float density = 16.0;


    // Normalized pixel coordinates (from 0 to 1)

    //scrolly cat and wave
    vec2 uv = fragCoord/iResolution.xy;

    uv.x-= iTime/8.0;

    vec4 c = texture(iChannel1, uv);
    if(sin(uv.x+iTime)/5.0 + 0.5 <= uv.y){
        c = initialcolor;
    }
    //end cat and wave

    // the important code
    if(c == initialcolor){
        if(mod(fragCoord.x,density) <= tolerance && mod(fragCoord.y,density) <= tolerance){
            c = replacecolor;

        } else{
            c = bgcolor;
        }
    }


    fragColor = c;
}
