void mainImage( out vec4 fragColor, vec2 u )
{
    float crop = 2.25;
    float isOut = 1.0; // 0 - In; 1 - Out

    float time = (min(iTime, 1.0) + isOut) * crop;

	vec2  R = iResolution.xy,
          U = 10.* ( u -.5*R ) / R.x * mat2(1,1,-1,1)*.75 + 5.,
          F = fract(U);
          F = min(F,1.-F);
    float s = length( ceil(U) - 5.5 ),
          e = 2.5* fract( ( time - s*.075 ) / 5.) - 1.,
          v = fract ( 4.* min(F.x,F.y) );


    float value = smoothstep( -.05, 0., .95*(e < 0. ? v : 1.-v) - e*e);


    fragColor = mix(texture(iChannel0, u/iResolution.xy), vec4(1.0), value);
}
