const float PI = 3.14159;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    float d = 0.0;
    int n = 8;
    vec3 col = vec3(0.);
    for (int i = 0; i < n; i++) {
        float findex = float(i) / float(n);
        float offset = 3. * findex * PI + 0.3 * iTime * (1. + findex * 0.3);
        float f = 0.5 * (1. + sin(offset));
        float d = abs(f - uv.y);
        float fcol = pow(1. - pow(d, 0.4), 4.);
        float r = 360. * findex * fcol;
        float b = i % 2 == 1 ? fcol * 0.2 : 0.;
        col += vec3(fcol, fcol * findex, b);
    }
    fragColor = vec4( col, 1.0 );
}
