void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float lw = 0.12;
    float x = float(uv.x + lw > uv.y && uv.x < uv.y + lw);
    float y = float(1.0 - uv.x + lw > uv.y && 1.0 - uv.x < uv.y + lw);

    // Output to screen
    fragColor = vec4(1.0, 1.0, 1.0,1.0) * (x + y) 
    + vec4(0.0, 0.368, 0.7215, 1.0);
}
