vec2 View(vec2 pixel)
{
    // Image rectangle corners are touching unit circle
    vec2 pn = (2.*pixel-iResolution.xy)/length(iResolution.xy);
    return (3.+sin(iTime))*pn;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 z = View(fragCoord);
    vec3 col = vec3(0);
    if(distance(z,vec2(+1,0))<0.1) col = vec3(1,0,0);
    if(distance(z,vec2(0,+1))<0.1) col = vec3(0,1,0);
    if(distance(z,vec2(0,-1))<0.1) col = vec3(0,0,1);

	//vec3 col = vec3(1., df2(p)/15.0);

    fragColor = vec4(col,1.);
}
