const float PI = 3.14159265359;
float rect(vec2 p, vec2 c, vec2 d){
    p = abs(p - c);
    return length(max(p - d, vec2(0.0))) + min(max(p.x - d.x, p.y - d.y), 0.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 pos = (2.0 * fragCoord.xy -iResolution.xy)/ iResolution.yy;
    float w = rect(pos, vec2(0.0), vec2(0.5));
    vec3 v = vec3(step(mod(w, 0.1), 0.01));  
    v += mix(vec3(0, 0, 1), vec3(1,0,0), atan(w)/PI + 0.5);
    v *= 1.0-step(abs(w),0.01);
    fragColor = vec4(v, 1.0);
}
