void mainImage( out vec4 o, in vec2 fragCoord)
{
    vec2 R = iResolution.xy;
    vec2 uv = fragCoord / R;
    uv.x *= R.x / R.y;

    float t = iTime * .6;
    uv *= 8.;
    uv -= vec2(t*.5, -t*.3);
    float ctime = cos(iTime);

    vec2 N = (fragCoord / R );
    float fM = mod(smoothstep(.0,.1,abs(N.y * N.x * ctime)), 0.06) * 1.5 * ctime;

    float fY = (N.y * 6.);
    float fX = (N.x * 6.);

    o = vec4(
        fM * fY * fX, 
        fM * fY, 
        fM * fX, 
        1
    );
}
