void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy / iResolution.xy; // get screen coordinates x & y in one line

    vec4 textColor = texture(iChannel0, xy); // get the pixel at xy from iChannel 0


	vec4 finalColor = textColor;
    finalColor.r = finalColor.r * distance(xy, vec2(sin(iTime), 0.0));
    finalColor.b = finalColor.b * distance(xy, vec2(cos(iTime), 1.0));
    finalColor.g = finalColor.g * distance(xy, vec2(0.0, sin(iTime)));

    fragColor = finalColor;
}
