void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy;
    xy.x = xy.x / iResolution.x;
    xy.y = xy.y / iResolution.y;

    vec4 texColor = texture(iChannel0,xy);

    float v = (texColor.r + texColor.b + texColor.g) / 3.0;

    texColor.r = texColor.r + abs(sin(iTime)) * (v - texColor.r);
    texColor.g = texColor.g + abs(sin(iTime)) * (v - texColor.g);
    texColor.b = texColor.b + abs(sin(iTime)) * (v - texColor.b);

    fragColor = texColor;
}
