info.description:
Trying to understand how to work with FFTs.
Reference shader used:
@rakeshcjadav: https://www.shadertoy.com/view/MdVSWG
@chronos: https://www.shadertoy.com/view/lsdGR8

renderpass.code:
#define PI 3.141592653
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = 2.0*uv - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    uv.x = abs(uv.x);
    //uv.y = abs(uv.y);

    //Uncommment these lines to switch to polar coordinates.
    //vec2 st = vec2((atan(uv.x,uv.y)+PI)/PI,length(uv));
    //uv = st;
    uv.x *= 0.250;
    float size = 0.01;
    vec2 sliceId = floor(uv/size);
    vec2 slicePos = (uv - sliceId*size)/size;
    uv = size*sliceId;

    float sample1 = texture(iChannel0, vec2((uv.x), 0.2)).x + 0.001;
    float sample2 = texture(iChannel0, vec2((uv.x), 0.2)).x + 0.04;

    vec3 col = 0.5 + 0.5*sin(vec3(uv.x,uv.y,0.) + iTime + vec3(0,2,4));
    vec3 color = vec3(0.0);

    //Idk how this part works
    //----------------------------
    float v = abs( uv.y);
    float bars = smoothstep(v,v*1.0, sample1);
    float bars2 = smoothstep(v,v*0.99, sample2);
    //----------------------------
    color += col*bars;
    color += vec3(1.)*bars2;


    fragColor = vec4(color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0,0.0,1.0,1.0);
}
