void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;

    float z = 1./abs(uv.x) * abs(uv.y);
    vec3 color = vec3(z);

    if (uv.y < 0.) {
        color = vec3(log(z * 10.));
    }

    fragColor = vec4(color, 1.);
}
