float sphr(vec2 p, float r)
{
    return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a=iResolution.x/iResolution.y;
    vec2 uv = (fragCoord/iResolution.xy)*2.-1.;
    uv.y += sin(iTime+uv.x*6.0)*0.2;

    bool hit = false;
    for(float f=0.; f<64.; f+=1.)
    {
        float t=f/64.*(3.14*2.-0.1);
        hit = hit || sphr(uv-vec2(sin(t),cos(t))*sin(iTime+3.14*(0.0125*f)),0.01)<0.;
    }

    fragColor = hit ? (0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4))).rgbr : vec4(0.);
    fragColor += textureLod(iChannel0, ((fragCoord)/iResolution.xy), 2.5)*0.99;
}
