void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    ivec2 fc= ivec2(fragCoord.xy);
    fc/=4;
    int m= fc.y%2;
    //fc.x+= int(iTime*4.);
    vec2 uv= vec2(fragCoord.xy)/vec2(iResolution.xy);
    fragColor= texture(iChannel0,uv)*vec4(fc.x%3==0,fc.x%3==1,fc.x%3==2,1.)*float(m);
}
