void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    vec4 foreground = texture(iChannel0, uv);
    vec4 background = texture(iChannel1, uv);

    float total = foreground.r + foreground.g + foreground.b;
    float greenPercentage = foreground.g / total;
    const float SENSITIVITY = 0.5;

    if (greenPercentage < SENSITIVITY) {
        fragColor = foreground;
    } else {
        fragColor = background;
    }
}
