/**
 * @author rah, inspired by jonobr1 / http://jonobr1.com/
 */

// Make a vec3 color from rgb values [0, 255]
vec3 rgb(float r, float g, float b)
{
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}

/* 
 * Output circle colors at given center pos and radius
 */
vec4 circle(vec2 uv, vec2 center, float radius, vec3 color)
{
	float d = length(center - uv) - radius;
	float t = clamp(d, 0.0, 1.0);
	return vec4(color, 1.0 - t);
}

/* 
 * Rotate an input point about some center by angle
 */
vec2 rotatePoint2d(vec2 uv, vec2 center, float angle)
{
    vec2 rotatedPoint = vec2(uv.x - center.x, uv.y - center.y);
    float newX = cos(angle) * rotatedPoint.x - sin(angle) * rotatedPoint.y;
    rotatedPoint.y = sin(angle) * rotatedPoint.x + cos(angle) * rotatedPoint.y;
    rotatedPoint.x = newX;
    return rotatedPoint;
}


float bias(float t, float b) {
    return (t / ((((1.0/b) - 2.0)*(1.0 - t))+1.0));
}

vec4 coolMistake1(out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy;
	vec2 center = iResolution.xy * 0.5;
	float radius = 0.25 * iResolution.y;

    // Background layer
	vec4 bg = vec4(rgb(255.0, 255.0, 210.0), 1.0);
	vec4 red = vec4(rgb(225.0, 50.0, 70.0), 1.0);
    vec4 color = red;

    // Get point normalized from center of the screen
    vec2 p = (2.0*fragCoord.xy-iResolution.xy);

    // angle of the point
    float a = atan(p.x,p.y);

    // scale the radius
    float r = length(p) * 0.75f;

    // interesting discontinuity
    float x = cos(a * 10.0f);
    //x += sin(a * 10.0f) * 0.5f;

    radius -= x * 40.0f * sin(iTime);


	// Circle
	vec4 layer2 = circle(uv, center, radius, vec3(color));

    radius -= 40.0f;
    vec4 layer3= circle(uv, center, radius, vec3(bg));

    return mix(bg, layer2, layer2.a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy;
	vec2 center = iResolution.xy * 0.5;
	float radius = 0.25 * iResolution.y;

    // Background layer
	vec4 bg = vec4(rgb(255.0, 255.0, 210.0), 1.0);
	vec4 red = vec4(rgb(160.0, 0.0, 70.0), 1.0);
    vec4 blue = vec4(rgb(125.0, 200.0, 250.0), 1.0);

    // Get point normalized from center of the screen
    vec2 p = (2.0*uv-iResolution.xy)/iResolution.y;

    // scale the radius
    float r = length(p) * 0.5f;

    // Spiraling animation

    // Create a "bump" signal based on distance from origin (sin pattern radiating out)
    float rotationFactor = cos(r * 6.0 + 1.0);
    // Intensify the bump amplitude the further you are from center, pow makes amplitude increase logrithmic
    rotationFactor *= pow(r, 0.5) ;
    // Oscillate the direction of the spin
    rotationFactor *= sin(iTime);
    // make it animate over time (spin!)
    rotationFactor += iTime;

    // rotate p around the origin using our spinning animation
    p = rotatePoint2d(p, vec2(0.0, 0.0), rotationFactor);

    // angle of the rotated point
    float a = atan(p.x,p.y);

    // create an interesting discontinuity
    float bumpFactor = bias(cos(a * 10.0), 0.9);
    // add an extra bumpy factor to exaggerate the points
    bumpFactor += sin(a * 10.0);

    // make bump factor amplitude oscillate
    float bumpFactorScale = sin(iTime) * 30.0;
    // Compute the radius
    radius -= bumpFactor * bumpFactorScale;

	// Color red or blue based on the positive or negative bump contribution
    vec4 color = mix(red, blue, pow(1.0-(bumpFactor * 0.5), 1.0));
	vec4 layer2 = circle(uv, center, radius, vec3(color));

    fragColor = mix(bg, layer2, layer2.a);
}
