void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    ivec2 fc= ivec2(fragCoord.xy);
    vec4 c= vec4(1.);
    int d= 2;

    switch(fc.y%3){
    default:
    case 0: d=2; fc.x+= int(iTime*11.); c= vec4(1.,0.,0.,1.); break;
    case 1: d=2; fc.x+= int(-iTime*12.); c= vec4(0.,1.,0.,1.); break;
    case 2: d=2; fc.y+= int(iTime*13.); c= vec4(0.,0.,1.,1.); break;
    }

    ivec2 m= fc%d;
    float l= float(m.x+m.y);
    l= 1.-l;

    fragColor= c*l;
}
