void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n_tiles = 20.0;

    float x = fragCoord.x / iResolution.x * n_tiles;
    float y = fragCoord.y / iResolution.x * n_tiles;
    // Stripe
    int key = int(mod(floor(x),5.0));
    vec3 color;
    switch (key)
    {
        case 0: color = vec3(1.0,0.0,0.0); break;
        case 1: color = vec3(0.0,1.0,0.0); break;
        case 2: color = vec3(0.0,0.0,1.0); break;
        case 3: color = vec3(1.0,1.0,0.0); break;
        case 4: color = vec3(0.0,1.0,1.0); break;
    }
    // Grid
    //if (bool(mod(floor(y),2.0)))
    //    color = 1.0 - color;
    if (bool(mod(floor(y),2.0)))
        switch (key)
        {
            case 0: color = vec3(0.0,1.0,1.0); break;
            case 1: color = vec3(1.0,0.0,0.0); break;
            case 2: color = vec3(0.0,1.0,0.0); break;
            case 3: color = vec3(0.0,0.0,1.0); break;
            case 4: color = vec3(1.0,1.0,0.0); break;
        }
    // Output to screen
    fragColor = vec4(color,1.0);
}

