void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float mouseControl = .0; //1.0 or .0, but works with any float
    vec2 c = fragCoord - (1.-mouseControl)*0.5*iResolution.xy; //definition of center coordinates [-Res.xy/2 ~ Res.xy/2]
        c -= iMouse.xy * mouseControl;
    float ang = atan(c.y,c.x); // [-pi ~ pi] angular coordinate
    float dist = sqrt(dot(c,c)); // [0 ~ inf] polar distance coordinate
    float spiral = dist*0.1 + .0*ang - (iTime); // spiral function
    float primary = ceil(sin(spiral)); // square wave from spiral function
    float invdist = (50./dist); // [0 ~ inf] 
    // invdist*=0.; // uncomment to separate center circle 
    float opposite = min(1.,ceil(invdist + sin( ang*5. + 0.5*iTime + 0.*sin(iTime)*sin(iTime))));
    float result = min(primary, opposite);
fragColor = vec4( result -0.5,  -0.5 ,  -0.5, 1.0); // output: R,G,B,alpha
}
