void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 aColor;

    float average = ((aColor.r + aColor.g + aColor.b) / 3.0);
    aColor.rgb = vec3(average);



    float Screen1WidthPercent = .5;

    if(uv.x <= Screen1WidthPercent)
    {
        fragColor = texture(iChannel0, uv);
    }
    else
    {
        vec3 colorChange = texture(iChannel1, uv).rgb;
        vec3 colorChange2 = vec3(1.0,2.0,1.0);
        vec3 col = colorChange * colorChange2;
        fragColor = vec4(col,1.0);
    }




}
