void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 d = 1.0 / iResolution.xy;
    vec2 uv = fragCoord.xy / iResolution.xy;    
    // init property
    if (iTime < 0.1)
    {
        if ((uv.x > 0.1) && (uv.x < 0.9) && (uv.y > 0.4) && (uv.y < 0.5))
            fragColor = vec4(0.0, 0.0, 1.0, 1.0);
        else
            fragColor = vec4(0.0, 0.0, 0.0, 1.0);
        return;
    }

    vec4 origin = texture(iChannel0, uv);
    float du = origin.y;
    // check mouse input position
    vec2 r = fragCoord.xy - iMouse.xy;
    float dist = 0.001 * dot(r, r);
    if (iMouse.z > 0.0 && dist < 0.2) {
        fragColor = vec4(0.0, 0.2, origin.zw);
        return;
    }
    // copy
    float _X = texture(iChannel0, vec2(uv.x + d.x, uv.y)).x;
    float _x = texture(iChannel0, vec2(uv.x - d.x, uv.y)).x;
    float _Y = texture(iChannel0, vec2(uv.x, uv.y + d.y)).x;
    float _y = texture(iChannel0, vec2(uv.x, uv.y - d.y)).x;
    vec2 n = -vec2((_X - _x) / d.x, (_Y - _y) / d.y);
    vec2 offset = n.xy;
    if ((abs(n.x) < 0.001) && (abs(n.y) < 0.001))
        offset = vec2(0);
    float np = texture(iChannel0, uv + offset * 0.01).z;   
    fragColor = vec4(origin.xy, np, origin.w);

    //fragColor = vec4(origin.xy, n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 d = 1.0 / iResolution.xy;
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 origin = texture(iChannel0, uv);

    float u = origin.x;
    float du = origin.y;

    float ux = texture(iChannel0, vec2(uv.x + d.x, uv.y)).x;
    float umx = texture(iChannel0, vec2(uv.x - d.x, uv.y)).x;
    float uy = texture(iChannel0, vec2(uv.x, uv.y + d.y)).x;
    float umy = texture(iChannel0, vec2(uv.x, uv.y - d.y)).x;
    float uxy = texture(iChannel0, vec2(uv.x + d.x, uv.y + d.y)).x;
    float umxy = texture(iChannel0, vec2(uv.x - d.x, uv.y + d.y)).x;
    float uxmy = texture(iChannel0, vec2(uv.x + d.x, uv.y - d.y)).x;
    float umxmy = texture(iChannel0, vec2(uv.x - d.x, uv.y - d.y)).x;


    // edge

    float nu = u + du + 0.25 * (umx + ux + umy + uy + 0.707 * (uxy + umxy + uxmy + umxmy) - 8.0 * u);
    //  u + du + 0.5 * (umx + ux + umy + uy - 4.0 * u);
    nu = 0.9 * nu;

    fragColor = vec4(nu, nu - u, origin.zw);
}
