// Produces a function which splits the color of the screen
vec4 splitScreen(vec4 col1, vec4 col2, float percent, vec2 fragCoord){
    float Screen1WidthPercent = percent;

    if(fragCoord.x > .5 - (.5*Screen1WidthPercent) && fragCoord.x < .5 + (.5*Screen1WidthPercent)){
        return col1;
    }
    else{
        return col2;
    }
}

// Produces a white background with a red line at x = 50;
vec4 redLine(vec4 inp, vec2 fragCoord){
    vec4 lineAdd = inp;

    if(fragCoord.x == 50.5){
        return vec4 (1, 0, 0, 1);
    }
    else{
        return lineAdd;
    }


}

// Brightens a texture by 10%
vec4 brightTexture(vec4 inp){
    vec4 texColor = inp;

    texColor.x *= 1.1;
    texColor.y *= 1.1;
    texColor.z *= 1.1;
    texColor.a *= 1.1;

    return texColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Returns the texture in iChannel1
    vec4 texColor = texture(iChannel0, uv.xy);

    // Sets the color red
    vec4 red = vec4(1,0,0,1);

    // Sets the color white
    vec4 white = vec4(1,1,1,1);

    // Output to screen
    fragColor = splitScreen(red, white, .4, uv.xy);
}
