void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5f*iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.0);
    uv *= 15.;
    vec2 gv = fract(uv) - 0.5f;
    vec2 id = floor(uv);

    float width = 0.1f;

    //gv.x *= -1.0f;
    float mask = smoothstep(0.01, -0.01, abs(gv.x+gv.y) - width);
    col.rg += id * 3.0f;

    if (gv.x > 0.48f || gv.y > 0.48f) col = vec3(0.,0.,1.0f);

    fragColor = vec4(col,1.0);
}
