#define PI 3.14159265

mat2 rotate2d(float a) {
	float c = cos(a); 
    float s = sin(a);
	return mat2(c, s, 
               -s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 5.0;
    vec2 uv = fragCoord/iResolution.y * scale ;
    float ratio = iResolution.x/iResolution.y;

    vec2 center = scale*vec2(ratio, 1.0)/2.0;

    float angle = PI/100. + PI*((sin(iTime)+1.)/6.);

    vec2 offset = center;
    //offset = vec2(2.0, 1.0);
    //offset = vec2(0);

    uv = (uv-offset) * rotate2d(angle) + offset;

    // Output to screen
    fragColor = texture(iChannel0, uv);
}
