float circle(in vec2 _st, in float _radius, in float feather){
    vec2 dist = _st-vec2(0.5);
	return 1.-smoothstep(_radius-(_radius*feather),
                         _radius+(_radius*feather),
                         dot(dist,dist)*4.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x*=1.667;
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float mask = circle(uv, 0.9, 0.05);
    mask -= circle(uv, 0.8, 1.0);
    float time = 0.0;


    float innerCircle = 0.0;
    for (float i = 0.0; i<5.0; i+=0.5) {
       innerCircle += circle(uv, fract(((iTime+i)/5.0))+0.05, 0.08)*0.2;
       innerCircle -= circle(uv, fract(((iTime+i)/5.0))+0.04, 0.08)*0.2;
    }
    mask+=circle(uv, 0.05, 0.4)*0.3;
    mask+=innerCircle;
    mask = clamp(0.0, 1.0, mask);
    col = vec3(mask)*vec3(0.4,1.0,0.9);
    // Output to screen
    fragColor = vec4(col,1.0);
}
