/// *** PERLIN ZAJ ***
/// 2D random
vec2 random2(vec2 c) { float j = 0.1*sin(dot(c,vec2(.007, 5.8))); vec2 r; r.x = fract(33333332.0*j); j *= .125; r.y = fract(512.0*j);return r-0.5;}

/// 1D random / hash
float hash(float f){ return fract(546.235423 * sin(f*2345.345345));}

float hash2(float f){ return fract(5336.235423 * sin(f*2345.345345));}

const float F2 =  0.3660254;
const float G2 = -0.2113249;

/// simplex 2D zaj; perlin zaj egy frekvenciajahoz tartozo ertekek
float simplex2d(vec2 p){vec2 s = floor(p + (p.x+p.y)*F2),x = p - s - (s.x+s.y)*G2; float e = step(0.0, x.x-x.y); vec2 i1 = vec2(e, 1.0-e),  x1 = x - i1 - G2, x2 = x - 1.0 - 2.0*G2; vec3 w, d; w.x = dot(x, x); w.y = dot(x1, x1); w.z = dot(x2, x2); w = max(0.5 - w, 0.0); d.x = dot(random2(s + 0.0), x); d.y = dot(random2(s +  i1), x1); d.z = dot(random2(s + 1.0), x2); w *= w; w *= w; d *= w; return dot(d, vec3(40.0));}

float fbm(vec2 n){
	float total = 0.0, amplitude = 99.71;
	for (int i = 0; i < 9; i++) {
		total += simplex2d(n) * amplitude;
		n += n;
		amplitude *= 0.43335;
	}
	return total;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float t = iTime*0.052; // let globally control the time
	float q = fbm(uv+t * 0.01);
    float v = fbm(uv + q + t * 0.97 - uv.x - uv.y) * 0.095 - 1.73173;
    float vNoise = hash2(v / 0.0005) * 0.856 + v;
    float v2 = fbm(uv + q * 0.5 + t * 0.3337 - uv.x - uv.y) * 0.0595 - 1.13173;
    float vNoise2 = hash2(v / 0.0005) * 0.856 + v;

    fragColor = vec4(v, v, v, 1.0);


}
