float metaball(vec2 p, vec2 pixel, float r)
{
    vec2 d = p-pixel;
	return r / dot(d,d);
}
float metaDiamond(vec2 p, vec2 pixel, float r)
{
    vec2 d = abs(p-pixel);
	return r / (d.x + d.y);
}
float metaDiamond2(vec2 p, vec2 pixel, float r)
{
    vec2 d = abs(p-pixel);
	return r / (d.x + d.y);
}
float metaEllipsey(vec2 p, vec2 pixel, float r, float xM, float yM)
{
    vec2 d = p-pixel;
    d.x /= xM;
    d.y /= yM;
	return r / dot(d,d);
}
float metaEllipsex(vec2 p, vec2 pixel, float r, float xM, float yM)
{
    vec2 d = p-pixel;
    d.x /= yM;
    d.y /= xM;
	return r / dot(d,d);
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	float r = metaball(iResolution.xy / 2., fragCoord, 200.);

	float r1 = metaDiamond(iResolution.xy / 2. + vec2(sin(iTime) * 200.,  0.), fragCoord+cos(.5*iTime)*iResolution.xy / 2., 20.);

    float r2 = metaDiamond(iResolution.xy / 2. + vec2(0., sin(iTime) * 200.), fragCoord+sin(.7*iTime)*iResolution.xy / 2., 20.);

    float r5 = metaDiamond(iResolution.xy / 2. + vec2(0., sin(iTime) * 200.), fragCoord+sin(.7*iTime)*vec2(iResolution.y,iResolution.x) / 2., 20.);

    float r6 = metaDiamond(iResolution.xy / 2. + vec2(sin(iTime) * 200.,0.), fragCoord+cos(.5*iTime)*vec2(iResolution.y,iResolution.x) / 2., 20.);

	float r3 = metaEllipsey(iResolution.xy / 2. + vec2(0., cos(iTime) * -200.), fragCoord, 50., 1., 5.);

    float r4 = metaEllipsex(iResolution.xy / 2. + vec2(sin(iTime) * 200.,0.), fragCoord, 50., 1., 5.);

    fragColor = vec4(vec3((r+r1+r2+r3+r4+r5+r6)*cos(iTime+vec3(0,2,4))), 1.0);
}
