void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float time = iTime;
 for(int n=1; n<20; n++)
   {
      float i = float(n);
      uv += vec2(
        cos (0.7+sin(i+uv.y+time+0.3)+0.4),
       atan     (0.4*sin(i*uv.x+time+0.3)+1.6)
      );
   }
   vec3 res = vec3(0.5+sin(uv.x)+0.5,
       0.5+sin(uv.y)+0.5,
       sin(uv.x+uv.y));
   fragColor = vec4(
       res/res.b,1.0);
}
