//robado:
float disk(vec2 r, vec2 center, float radius) {
	return 1.0 - smoothstep( radius-0.5, radius+0.5, length(r-center));
}

// texture 1 , rainbow 0
#define TX 1


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float disc = disk(fragCoord.xy,iMouse.xy,90.0);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy / sin(iResolution.x + iResolution.y) ;
    vec2 uu = vec2(fragCoord.x / iResolution.x , fragCoord.y / iResolution.y);
    //uv.x += 0.1* iTime;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    vec3 ext =abs(col) + sin( iTime + uv.xyx + uv.x * uv.y - length(cos(col.xyx * uv.xyx)) * 5.0 );

    vec3 text ;
    float num = 0.0;
    if(TX == 0) {
    text = col;
    num = 3.0;
    } else {
    text = texture(iChannel0 , uu + 0.1 * sin(iTime + uv.xy - col.xy ) ).xyz;
    num = 8.0;
    }

    // anim
    text = text + cos(15.0 * length(text) + iTime * num )   ;
    // color
    text = exp(text - 0.5)  ;



    vec3 f ;

    vec3 cosa = smoothstep(text,ext,col) + mix(ext,col,text) * ext; 
    cosa = length(f) - f - 1.0;


    float num2 = cos(iTime * 2.0) * 20.0 + 80.0;

    if(uu.x > disk(fragCoord.xy,iMouse.xy,num2)   ) {
    f =  text / length(ext) * exp(ext) ;


    } else {
    f = text / length(ext); //* vec3(0.3,0.5,0.9) ;
    }


    // Output to screen
    fragColor = vec4(f,1.0);
}
