void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 uvOffset = texture(iChannel0, uv * 0.2 + iTime * 0.01).rr * 2.0 - 1.0;

    float myTex = texture(iChannel0, uv + uvOffset * vec2(0.0, 0.2)).r + iTime * 0.1;
    vec2 lutUV = vec2(myTex, 0.5);
    vec3 lut = texture(iChannel1, lutUV * 0.2).rgb;

    vec3 col = lut;

    // Output to screen
    fragColor = vec4(col,1.0);
}
