float drawCircle(vec2 center, float radius, vec2 uv)
{
    return step(distance(center, uv), radius);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 aspect = vec2(1.0, iResolution.y / iResolution.x);
    uv *= aspect;

    // 1
    vec3 circle2Color = vec3(255.0,0.0,0.0);
    float circle2 = drawCircle(vec2(0.5,(cos(9.0 * iTime)*0.1+0.5)) * aspect, 0.1, uv);


    // 2

    vec3 PulseCol = vec3(1.0*cos(iTime*10.0),1.0*cos(iTime*5.0),1.0*cos(iTime*5.0));

    // 3

    vec4 circle3Color = vec4(0.0,255.0,0.0,1.0);
    float circle3 = drawCircle(vec2(0.5,(cos(3.0 * iTime)*0.1+0.5)) * aspect, 0.1, uv);
    vec4 circle4Color = vec4(0.0,0.0,0.0,255.0);
    float circle4 = drawCircle(vec2((cos(5.0 * iTime)*0.1+1.5),1.45) * aspect, 0.1, uv);
    vec4 circle5Color = vec4(0.0,0.0,255.0,255.0);
    float circle5 = drawCircle(vec2(0.5,(cos(8.0 * iTime)*0.1+0.5)) * aspect, 0.1, uv);

    vec3 col;
    col = mix(vec3(0.0, 0.0, 0.0), PulseCol, circle2);


    vec3 col2 = mix(vec3(0.0, 63.0, 0.0), PulseCol, circle3);
    vec3 col3 = mix(vec3(0.0, 0.0, 0.0), PulseCol, circle4);
    vec3 col4 = mix(vec3(0.0, 0.0, 0.0), PulseCol, circle5);
    // 4

    vec3 col5;
    float Screen1WidthPercent = .5;

    if(uv.x <= Screen1WidthPercent)
    {
        /*vec3 tex = sin(iTime + texture(iChannel0, uv));
        col5 = tex;*/
    }
    else
    {
        vec3 colorChange = texture(iChannel1, uv).rgb;

        col5 = sin(iTime + colorChange);

    }



    // Output to screen
    fragColor = vec4(col + col2 + col3 + col4 + col5,1.0);
}
