void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Q.1. Grayscale
    vec4 channel0 = texture(iChannel0, uv);
    float rgbAvg = (channel0.r + channel0.g + channel0.b) / 3.0;
    channel0 = vec4(rgbAvg, rgbAvg, rgbAvg, 1.0);


    // Q.2. Split-screen
    vec4 channel1 = texture(iChannel1, uv);

    if (uv.x < 0.5) {
        fragColor = channel0;
    } else {
       // Q.3 Tinting
       channel1 = vec4(channel1.r * 1.15, channel1.g, channel1.b, 1.0);
       fragColor = channel1;
    }


    // Output to screen

}
