void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float len = 0.46;
    float PI = 3.14;
    float offset = 0.4;

    if(uv.y>offset && uv.y<offset + len  ){
         fragColor = vec4(.5*cos(6.283*(uv.x + iTime  +vec3(0.,-.33333,.33333)))+.5,1.0);
         float x = uv.y-offset;

         float aa = sin(  x * PI/len  )  ;

         vec4 vv = vec4(aa ,aa,aa,0  );

         fragColor += vv;

    }else{

             fragColor = vec4( 0.0 ,0.0 ,0.0,1.0);


    }

}
