#define SCALE 6.0
#define PI radians(180.0)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 m = iMouse; m.xy = m.xy*2.0/iResolution.xy-1.0; // ±1x, ±1y
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y*SCALE; // apply cartesian & scale
    if (m.z > 0.0) uv *= pow(1.0-abs(m.y), 4.0*sign(m.y)); // mouse y zoom
    float x = uv.x; // every x
    float y = uv.y; // every y
    vec3 c = vec3(0); // black background
    float t = iTime/4.0;
    float eq; // equation

    //eq = x; // x grid
    //eq = x*y; // multiply grid
    //eq = x*x + y*y; // sqrt sum grid
    //eq = x*x - y*y; // sqrt difference grid
    //eq = log(abs(y))/log(abs(x)); // power series
    //eq = pow(abs(y), 1.0/x); // exponential series
    eq = pow(abs(x), 1.0/y); // logarithmic series
    //eq = (x*x+y*y-1.0)/x; // metallic ratios

    float sg = min(abs(eq), 1.0/abs(eq)); // smooth gradient
    float og = abs(sin(eq*PI-t)); // oscillating gradient
    og = min(og, abs(sin(PI/eq+t))); // add reciprocals
    og = 1.0-og; // swap black & white
    og = pow(og, 4.0); // thin bright lines
    c += og; // add oscillating gradient to background
    c *= sg; // combine with smooth gradient
    c *= vec3(0.3, 0.5, 0.9); // multiply with color
    c *= 5.0; // brighten

    vec2 xyg = abs(fract(uv+0.5)-0.5)/length(fwidth(uv)); // xy grid
    c = mix(c, vec3(0), 1.0-min(min(xyg.x, xyg.y), 1.0)); // lines

    fragColor = vec4(c, 1.0);
}
