const float pi = 3.141592654;
const vec4 col1 = vec4(0.0, 80.0/255.0, 160.0/255.0, 1.0);
const vec4 col2 = vec4(70.0/255.0, 128.0/255.0, 190.0/255.0, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.yy;
	float r = length(uv);

    float theta1 = atan(uv.y, uv.x);   
    float alpha1 = abs((fract(10.0 * theta1 / pi + 8.0 * pow(r, 0.4) - 0.05 * iTime) - 0.5)*2.0);
    alpha1 = smoothstep(0.5 - 0.01/r, 0.5 + 0.01/r, alpha1) * 0.25;

    float theta2 = atan(uv.y, uv.x);   
    float alpha2 = abs((fract( 10.0 * theta2 / pi + 7.0 * pow(r, 0.4) + 0.1 * iTime) - 0.5)*1.5);
    alpha2 = smoothstep(0.5 - 0.01/r, 0.5 + 0.01/r, alpha2) * 0.25;

    float a = alpha1 + alpha2;
    fragColor = mix(col1, col2, a);

}
