void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    //recenter to width/2 - height/2
    fragCoord.xy -= vec2(iResolution.x * 0.5, iResolution.y * 0.5);

    // Calculate angle based on pixel position
    float angle = (atan(fragCoord.x, fragCoord.y) + 3.14159265358979)/(3.14159265358979*2.0);

    // Output to screen
    fragColor = vec4(angle,angle,angle,1);
}
