#define R                           iResolution
#define TO_CENTERED_NDC(SC)       ( ((SC)+(SC)-R.xy) / R.y )         // in range x=[-AR; AR], y=[-1.0; 1.0]
#define NZMX                      ( (iMouse.x+iMouse.x-R.x) / R.x )  // in range [-1.0; 1.0]
#define NZMY                      ( (iMouse.y+iMouse.y-R.y) / R.y )  // in range [-1.0; 1.0]
#define TAU                         6.2831853071795862
#define INITIALLY_FAR               9e30
#define LMB_IS_HOLD               ( iMouse.z > 0.0 )

float L2(vec2 B, vec2 E)
{
    vec2 D = E - B;
    return sqrt(D.x*D.x + D.y*D.y);
}

float quadraticSDF_L2(
    vec2 NDC,
    float n, // acceleration
    float m, // slope
    float k)
{
    float a = NDC.x;
    float b = NDC.y;
    float coef[4] = float[4](
        2. * n * n,
        3. * n * m,
        2. * n * k - 2. * n * b + m * m + 1.,
        m * k - m * b - a
    );
    vec2 roots[3];

    int case_ = solveCubicVieta(roots, coef);

    float minSq = INITIALLY_FAR;

    for (int i = 0; i < case_; ++i)
    {
        // Possibly closest point of curve.
        vec2 P = vec2(roots[i].x, n * roots[i].x * roots[i].x + m * roots[i].x + k);

        vec2 diff = P - NDC;

        // Find minimal squared distance
        minSq = min(dot(diff, diff), minSq);
    }

    float R = sinOSC(0.0, 0.25, iTime);
    return sqrt(minSq) - R;
}

/* IQ coloring scheme */
vec3 colorize_field(float dist, vec3 color)
{
    // Change color if dist is negative.
    color = 1.0 - sign(dist) * color;

    // Attenuate color around edges.
	color *= 1.0 - exp(-4.0 * abs(dist));

    // Add interleaved curves.
	color *= 0.7 + 0.2 * cos(30.0 * dist);

    // Add white edges.
    return mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 6./R.y, abs(dist)));
}

vec3 add_metric_circle(
    vec2 NDC,
    float n,
    float m,
    float k,
    vec3 color)
{
    float unit = 7.0 / R.y;

    vec2 M = 
        LMB_IS_HOLD ?
            TO_CENTERED_NDC(iMouse.xy) :
            rotateAroundZ(vec2(1, 0), iTime * 0.5) * 1.5;
            //vec2(0);

    // Add point
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(NDC - M) - 0.008));

    float dist_form_point_to_box_edge = L2(NDC, M) - abs(quadraticSDF_L2(M, n, m, k));
    float outline = abs(dist_form_point_to_box_edge);

    // Add metric circle
    return mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, outline));
}
/* IQ coloring scheme */

void mainImage( out vec4 O, in vec2 SC )
{
    float n = sinOSC(-4.0, 4.0, iTime * 0.4);
    float m = sinOSC(-4.0, 4.0, iTime * 0.8);
    float k = 0.0;

    vec2 NDC = TO_CENTERED_NDC(SC) * 2.0;
    float minDist = quadraticSDF_L2(NDC, n, m, k);

    // Blending.
    vec3 color = colorize_field(minDist, vec3(0.1, 0.4, 0.7));
    color = add_metric_circle(NDC, n, m, k, color);

    O = vec4(color, 1.0);
}

/* Returns sine modulated value in range [min, max]. */
float sinOSC(in float min, in float max, in float T)
{
    float v = (sin(T) + 1.0) * 0.5; // map T to [0.0, 1.0];
    return min + v * (max - min);   // map T to [min, max];
}

vec2 rotateAroundZ(vec2 p, float a)
{
    float s = sin(a);
    float c = cos(a);

    return vec2(
        p.x * c - p.y * s,
        p.y * c + p.x * s);
}


/*
    Calculates cube root with Newton-Raphson method.
*/
float cubeRoot(const float x)
{
    float xc = x;
    float xxc = x * x;

    for (int i = 0; i < 2; ++i)
    {
        xc = (2.0 * xc + x / xxc) * 0.33333333333333331;
        xxc = xc * xc;
    }

    return xc;
}

/*
    Return value:
        1 - one real, two complex roots;
        2 - three real roots, but root[1] == root[2];
        3 - three real roots.
*/
int solveCubicVieta(
    inout vec2[3] roots,
    in float[4] coef)
{
    const float oneThird = 0.33333333333333331;
    const float TAU = 6.2831853071795862;

    float a = coef[1] / coef[0];
    float b = coef[2] / coef[0];
    float c = coef[3] / coef[0];

    float Q = (a * a - 3.0 * b) / 9.0;
    float R = (2.0 * a * a * a - 9.0 * a * b + 27.0 * c) / 54.0;

    float S = Q * Q * Q - R * R;

    if (S > 0.0)
    {
        float phi = oneThird * acos(R / sqrt(Q * Q * Q));
        float Re = -2.0 * sqrt(Q);

        roots[0].x = Re * cos(phi) - a * oneThird;
        roots[0].y = 0.0;
        roots[1].x = Re * cos(phi + TAU * oneThird) - a * oneThird;
        roots[1].y = 0.0;
        roots[2].x = Re * cos(phi - TAU * oneThird) - a * oneThird;
        roots[2].y = 0.0;

        return 3;
    }

    if (S < 0.0 && Q > 0.0)
    {
        float phi = oneThird * acosh(abs(R) / sqrt(Q * Q * Q));
        float signR = R < 0.0 ? -1.0 : 1.0;

        float sqrtQ = sqrt(Q);
        float Re = signR * sqrtQ * cosh(phi);
        float Im = sqrtQ * sinh(phi);
        float sqrt3 = 1.7320508075688772;

        roots[0].x = -2.0 * Re - a * oneThird;
        roots[0].y = 0.0;
        roots[1].x = Re - a * oneThird;
        roots[1].y = sqrt3 * Im;
        roots[2].x = Re - a * oneThird;
        roots[2].y = -sqrt3 * Im;

        return 1;
    }

    if (S < 0.0 && Q < 0.0)
    {
        float phi = oneThird * asinh(abs(R) / sqrt(abs(Q * Q * Q)));
        float signR = R < 0.0 ? -1.0 : 1.0;

        float sqrtQ = sqrt(abs(Q));
        float Re = signR * sqrtQ * sinh(phi);
        float Im = sqrtQ * cosh(phi);
        float sqrt3 = 1.7320508075688772;

        roots[0].x = -2.0 * Re - a * oneThird;
        roots[0].y = 0.0;
        roots[1].x = Re - a * oneThird;
        roots[1].y = sqrt3 * Im;
        roots[2].x = Re - a * oneThird;
        roots[2].y = -sqrt3 * Im;

        return 1;
    }

    if (Q == 0.0)
    {
        float Re = -cubeRoot(c - a * a * a / 27.0) - a / 3.0;
        float Im = sqrt(abs(a - 3.0 * Re) * (a + Re) - 4.0 * b) * 0.5;

        roots[0].x = Re;
        roots[0].y = 0.0;
        roots[1].x = -(a + Re) * 0.5;
        roots[1].y = Im;
        roots[2].x = -(a + Re) * 0.5;
        roots[2].y = Im;

        return 1;
    }

    float signR = R < 0.0 ? -1.0 : 1.0;
    float Re = signR * sqrt(Q);

    roots[0].x = -2.0 * Re - a * oneThird;
    roots[0].y = 0.0;
    roots[1].x = Re - a * oneThird;
    roots[1].y = 0.0;
    roots[2] = roots[1];

    return 2;
}
