vec3 firstColorlo = vec3(.18,.47,.86);
vec3 middleColorlo = vec3(.16,.65,1.0);
vec3 endColorlo = vec3(.54,1.0,1.0);

vec3 firstColorlt = vec3(.08,.38,.63);
vec3 middleColorlt = vec3(.16,.65,1.0);
vec3 endColorlt = vec3(.54,1.0,1.0);

float densityLayer1 = 10.0;
float densityLayer2 = 6.0;

int pixellateX = 5;
int pixellateY = 4;

vec2 pseudoRand(vec2 p) {
	return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

// https://iquilezles.org/articles/voronoilines
vec3 vorobord( in vec2 x ) {
    vec2 idx = floor(x);
    vec2 pad = fract(x);

	vec2 selfcell, closecell;

    float mindist = 4.0;
    for( float x = -1.0; x <= 1.0; ++x) {
        for(float y = -1.0; y <= 1.0; ++y) {
            vec2 offset = vec2(x, y);
            vec2 self = pseudoRand( idx + offset );
            self = 0.5 + 0.5*sin( iTime * self * .5 );
            vec2 neighbor = offset + self - pad;
            float dist = length(neighbor);

            if (dist < mindist) {
                mindist = dist;
                closecell = neighbor;
                selfcell = offset;
            }
        }
    }

    mindist = 4.0;
    for(float x = -2.0; x <= 2.0; ++x) {
        for(float y = -2.0; y <= 2.0; ++y) {
            vec2 offsetcell = selfcell + vec2(x, y);
            vec2 self = pseudoRand(idx + offsetcell);
            self = 0.5 + 0.5*sin(iTime * self  * 0.5);
            vec2 neighbor = offsetcell + self - pad;

            if(length(closecell - neighbor) > 0.00001){
                mindist = sin(min(mindist, dot(0.5 * (closecell + neighbor), normalize(neighbor - closecell))));
            }
        }
    }

    return vec3(mindist, closecell);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // Pixellate
    fragCoord.x -= (float(int(fragCoord.x * 1.0) % pixellateX) / 1.0);
    fragCoord.y -= (float(int(fragCoord.y * 1.0) % pixellateY) / 1.0);

    vec2 uv = fragCoord / iResolution.xy;

    // Animate the texture for a water ripple
    vec3 displ = vec3(texture(iChannel0, (uv * 0.25) + (sin(iTime * .15) * 0.25) + 0.25).xyz);

    // Create a voronoi pattern
    vec3 layerone = vorobord(densityLayer1 * mix(uv, displ.rg, 0.12));
    vec3 layertwo = vorobord(densityLayer2 * mix((uv + .5), displ.rg, 0.1));

    // Color the the upper portion
    // Based on y position, fade from white to blue
    vec3 upperColor1 = vec3(.24, .8, 1.0);
    upperColor1 += (uv.y/2.);

    vec3 upperColor2 = vec3(.16, .65, 1.);
    upperColor2 += (uv.y);

    // Gradient a base water color
    // Mercilessly ganked from https://www.shadertoy.com/view/ttB3Rh
    vec2 xy = fragCoord.xy / iResolution.xy;
    float h = 0.5; // adjust position of middleColor
    vec3 waterColorlo = mix(mix(firstColorlo, middleColorlo, xy.y/h), mix(middleColorlo, endColorlo, (xy.y - h)/(1.0 - h)), step(h, xy.y));
    vec3 waterColorlt = mix(mix(firstColorlt, middleColorlt, xy.y/h), mix(middleColorlt, endColorlt, (xy.y - h)/(1.0 - h)), step(h, xy.y));  

    // Mix the upper layer with water color
    vec3 locol = mix(upperColor1, waterColorlo, smoothstep(0.09, 0.1, layerone.x * 2.2));
    locol = mix(upperColor2, locol, smoothstep(0.09, 0.1, layerone.x * 4.0));

    // Mix the lower layer with water color
    vec3 ltcol = mix(vec3(.8,.8,1.0), vec3(1.0, 1.0, 1.0), smoothstep(0.04, 0.04, layertwo.x * 0.3));
    ltcol = mix(waterColorlt, ltcol, smoothstep(0.09, 0.1, layertwo.x * 2.0));

    // Mix uppper water with lower water
    vec3 vorolayers = mix(locol, ltcol, 0.3);

    fragColor = vec4(vorolayers, 1.0);
}
