// from iq 
// https://www.shadertoy.com/view/WldGWM

#define PI 3.141592653


vec3 sdJoint2DFlat( in vec2 p, in float l, in float a, float w)
{
    // if perfectly straight
    if( abs(a)<0.000001 )
    {
        float v = p.y;
        p.y -= clamp(p.y,0.0,l);
		return vec3( length(p), p.x, v );
    }

    // parameters
    vec2  sc = vec2(sin(a),cos(a));
    float ra = 0.5*l/a;

    // recenter
    p.x -= ra;

    // reflect
    vec2 q = p - 2.0*sc*max(0.0,dot(sc,p));

	// distance
    float u = abs(ra)-length(q);
    float d = max(length( vec2(q.x+ra-clamp(q.x+ra,-w,w), q.y) )*sign(-q.y),abs(u) - w);

    // parametrization (optional)
    float s = sign(a);
    float v = ra*atan(s*p.y,-s*p.x);
    u = u*s;

    return vec3( d, u, v );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    // animation
    float le = 3.0;
    float an = sin(iTime)*PI;
	float wi = 0.01;

    // distance and parametrization

    vec3 duv = sdJoint2DFlat(  uv-vec2( 0.0,-0.5),le,an,wi);

    // round
    float d = duv.x;

    // coloring
    vec3 col = vec3(1.0);
    if( d<0.0 )
    {
        duv.y = (duv.y - 0.5);
        col = vec3(0.);
    }

	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

	fragColor = vec4(col, 1.0);
}
